; Counting by basic string manipulation
(defn count-dna-string [s]
  (let [molecules (clojure.string/split s #"")
        count-mol (fn [m] (count (filter (partial = m) molecules)))]
    (clojure.string/join " " (map count-mol ["A" "C" "G" "T"]))))

(defn count-dna [s]
  (map count (partition-by identity  (sort s))))


(println (count-dna "")) ; => 230 241 227 233


(println (count-dna ""))

